import React from "react";
import "./App.css";
import UserList from "./components/Users";
import ProjectList from "./components/Projects";
import TodoList from "./components/Todos";
import MenuList from "./components/Menu";
import Footer from "./components/Footer";
import axios from "axios";
import { BrowserRouter, Route, Switch, Redirect } from "react-router-dom";

const NotFound404 = ({ location }) => {
  return (
    <div>
      <h1>Страница по адресу '{location.pathname}' не найдена</h1>
    </div>
  );
};

class App extends React.Component {
  constructor(props) {
    super(props);
    this.state = {
      users: [],
      todos: [],
      projects: [],
      menu: [],
      textFooter: "Footer text about something.",
    };
  }
  componentDidMount() {
    const menu = [
      { id: 1, link: "/", title: "Home", isActive: true },
      { id: 2, link: "u/sers", title: "Users", isActive: false },
      { id: 3, title: "Todos", isActive: false },
      { id: 4, title: "Projects", isActive: false },
    ];
    this.setState({ menu: menu });
    axios
      .get("http://127.0.0.1:8000/api/users/")
      .then((response) => {
        const users = response.data.results;
        this.setState({
          users: users,
        });
      })
      .catch((error) => console.log(console.error()));
    axios
      .get("http://127.0.0.1:8000/api/projects/todo/")
      .then((response) => {
        const todos = response.data.results;
        this.setState({
          todos: todos,
        });
      })
      .catch((error) => console.log(console.error()));
    axios
      .get("http://127.0.0.1:8000/api/projects/project/")
      .then((response) => {
        const projects = response.data.results;
        this.setState({
          projects: projects,
        });
      })
      .catch((error) => console.log(console.error()));
  }

  render() {
    return (
      <div className="container text-center">
        <BrowserRouter>
          <Switch>
            <Route path="/author/:id">
              <div className="navbar">
                <MenuList menu={this.state.menu} />
              </div>
            </Route>
            <Route
              exact
              path="/users"
              component={() => <UserList users={this.state.users} />}
            />
            <Route
              exact
              path="/projects"
              component={() => <ProjectList projects={this.state.projects} />}
            />
            <Route
              exact
              path="/"
              component={() => <TodoList todos={this.state.todos} />}
            />
            <Redirect from="/todos" to="/" />
            <Route component={NotFound404} />
          </Switch>
        </BrowserRouter>
        <div className="navbar">
          <Footer textFooter={this.state.textFooter} />
        </div>
      </div>
    );
  }
}
export default App;
