import React from "react";

class ProjectForm extends React.Component {
  constructor(props) {
    super(props);
    this.state = {
      title: "",
      link: "https://",
      usersWorked: props.usersWorked,
    };
  }
  handleChange(event) {
    this.setState({ [event.target.name]: event.target.value });
  }
  handleUsersWorkedChange(event) {
    if (!event.target.selectedOptions) {
      this.setState({ usersWorked: [] });
      return;
    }
    let usersWorked = [];
    for (let i = 0; i < event.target.selectedOptions.length; i++) {
      usersWorked.push(event.target.selectedOptions.item(i).value);
    }
    this.setState({ usersWorked: usersWorked });
  }
  handleSubmit(event) {
    console.log(this.state.title);
    console.log(this.state.link);
    console.log(this.state.usersWorked);
    event.preventDefault();
  }
  render() {
    return (
      <form onSubmit={(event) => this.handleSubmit(event)}>
        <div className="form-group py-2">
          <label htmlFor="title" className="mx-2">
            title:
          </label>
          <input
            type="text"
            name="title"
            value={this.state.title}
            onChange={(event) => this.handleChange(event)}
          />
        </div>
        <div className="form-group py-2">
          <label htmlFor="link" className="mx-2">
            link:
          </label>
          <input
            type="text"
            name="link"
            value={this.state.link}
            onChange={(event) => this.handleChange(event)}
          />
        </div>
        <div className="form-group py-2">
          <label htmlFor="usersWorked">Users Worked</label>
          <select
            multiple
            name="usersWorked"
            onChange={(event) => this.handleChange(event)}
          >
            {this.props.usersWorked.map((user) => (
              <option key={user.id.toString()} value={user.id}>
                {user.username}
              </option>
            ))}
          </select>
        </div>
        <input type="submit" className="btn btn-primary" value="Save" />
      </form>
    );
  }
}

export default ProjectForm;
