import React from "react";

class TodoForm extends React.Component {
  constructor(props) {
    super(props);
    this.state = {
      title: "",
      text: "",
      isActive: true,
      author: "",
      project: "",
    };
  }
  handleChange(event) {
    console.log(this.state);
    this.setState({ [event.target.name]: event.target.value });
  }
  handleAuthorChange(event) {
    if (!event.target.selectedOptions) {
      this.setState({ author: this.props.users[0].id });
      return;
    }
    let author = [];
    for (let i = 0; i < event.target.selectedOptions.length; i++) {
      author.push(event.target.selectedOptions.item(i).value);
    }
    console.log(author);
    this.setState({ author: author[0] });
  }
  handleProjectChange(event) {
    console.log(event.target.value);
    if (!event.target.selectedOptions) {
      this.setState({ project: this.props.projects[0].id });
    } else {
      this.setState({ project: event.target.value });
    }
  }
  handleSubmit(event) {
    this.props.createTodo(
      this.state.title,
      this.state.text,
      this.state.isActive,
      this.state.author,
      this.state.project
    );
    event.preventDefault();
  }

  toggleChange = () => {
    this.setState({
      isActive: !this.state.isActive,
    });
  };
  render() {
    return (
      <div className="card">
        <h1>Create project</h1>
        <form onSubmit={(event) => this.handleSubmit(event)}>
          <div className="form-group py-2 col-6 offset-3">
            <label htmlFor="title" className="mx-2">
              title:
            </label>
            <input
              type="text"
              name="title"
              className="form-control"
              value={this.state.title}
              onChange={(event) => this.handleChange(event)}
            />
          </div>
          <div className="form-group py-2 col-6 offset-3">
            <label htmlFor="text" className="mx-2">
              text:
            </label>
            <textarea
              type="text"
              name="text"
              className="form-control"
              value={this.state.text}
              onChange={(event) => this.handleChange(event)}
            />
          </div>
          <div className="form-group py-2 col-6 offset-3">
            <label htmlFor="isActive" className="mx-2">
              isActive Todo?:
            </label>
            <input
              type="checkbox"
              defaultChecked={this.state.isActive}
              onChange={(event) => this.handleChange(event)}
            />
          </div>
          <div className="form-group pa-2 col-6 offset-3">
            <label htmlFor="author" className="mx-2">
              author:
            </label>
            <select
              className="form-control"
              name="author"
              onChange={(event) => this.handleAuthorChange(event)}
            >
              {this.props.users.map((user) => (
                <option key={user.id.toString()} value={user.id}>
                  {user.username}
                </option>
              ))}
            </select>
          </div>
          <div className="form-group pa-2 col-6 offset-3">
            <label htmlFor="project" className="mx-2">
              project:
            </label>
            <select
              className="form-control"
              name="project"
              value={this.value}
              onChange={(event) => this.handleProjectChange(event)}
            >
              {this.props.projects.map((project) => (
                <option key={project.id.toString()} value={project.id}>
                  {project.title}
                </option>
              ))}
            </select>
          </div>
          <div className="py-2">
            <input type="submit" className="btn btn-primary" value="Save" />
          </div>
        </form>
      </div>
    );
  }
}

export default TodoForm;
