import React from "react";

class TodoForm extends React.Component {
  constructor(props) {
    super(props);
    this.state = {
      title: "",
      text: "",
      isActive: true,
      author: "",
      project: "",
    };
  }
  handleChange(event) {
    this.setState({ [event.target.name]: event.target.value });
  }

  handleSubmit(event) {
    event.preventDefault();
  }
  render() {
    return (
      <div className="card">
        <h1>Create project</h1>
        <form onSubmit={(event) => this.handleSubmit(event)}>
          <div className="form-group py-2 col-6 offset-3">
            <label htmlFor="title" className="mx-2">
              title:
            </label>
            <input
              type="text"
              name="title"
              className="form-control"
              value={this.state.title}
              onChange={(event) => this.handleChange(event)}
            />
          </div>
          <div className="form-group py-2 col-6 offset-3">
            <label htmlFor="text" className="mx-2">
              link:
            </label>
            <textarea
              type="text"
              name="text"
              className="form-control"
              value={this.state.text}
              onChange={(event) => this.handleChange(event)}
            />
          </div>
          <div className="form-group pa-2 col-6 offset-3">
            <label htmlFor="author" className="mx-2">
              Author:
            </label>
            <select
              className="form-control"
              name="author"
              onChange={(event) => this.handleUsersWorkedChange(event)}
            >
              {this.props.users.map((user) => (
                <option key={user.id.toString()} value={user.id}>
                  {user.username}
                </option>
              ))}
            </select>
          </div>
          <input type="submit" className="btn btn-primary" value="Save" />
        </form>
      </div>
    );
  }
}

export default TodoForm;
