import React, { useState } from "react";
import ProjectForm from "./ProjectForm";
import { useParams, useHistory } from "react-router-dom";
import { Link } from "react-router-dom";

function EditItem(props) {
  return <button onClick={props.onClick}>Войти</button>;
}
const ProjectItem = ({ users, project, deleteProject, editProject }) => {
  let { id } = useParams();
  let { location } = useHistory();
  let link, title;
  if (id) {
    link = <EditItem onClick={console.log("!!!")} />;
    title = <input type="text" value={project.title} />;
  } else {
    link = <Link to={`${location.pathname}/${project.id}`}>Edit Project</Link>;
    title = project.title;
  }
  return (
    <tr>
      <td>{project.id}</td>
      <td>{title}</td>
      <td>{project.link}</td>
      <td>{project.usersWorked.join(", ")}</td>
      <td>{link}</td>
      <td>
        <button type="button" onClick={() => deleteProject(project.id)}>
          Delete
        </button>
      </td>
    </tr>
  );
};

const ProjectList = ({ users, projects, deleteProject, editProject }) => {
  const [input, setInput] = useState("");
  let { id } = useParams();
  let filtered_items = projects;
  if (!id) {
    filtered_items = projects.filter(
      (item) => (item.title === "") | item.title.toLowerCase().includes(input)
    );
  }
  return (
    <div>
      {!id && (
        <div className="row pt-3 px-3 justify-content-end">
          <label
            htmlFor="search_title"
            className="col-2 justify-content-end  align-self-end"
          >
            Search title project:
          </label>
          <input
            type="text"
            name="search_title"
            className="col-3 justify-content-end  align-self-end"
            placeholder="Search"
            value={input}
            onChange={(e) => setInput(e.target.value)}
          />
        </div>
      )}
      <Link to="/create/project">Create Project</Link>
      <table className="table table-bordered my-3">
        <thead>
          <tr>
            <th>ID</th>
            <th>Title</th>
            <th>Link</th>
            <th>UsersWorked</th>
            <th></th>
          </tr>
        </thead>
        <tbody>
          {filtered_items.map((project) => (
            <ProjectItem
              project={project}
              deleteProject={deleteProject}
              editProject={editProject}
              users={users}
              key={project.id.toString()}
            />
          ))}
        </tbody>
      </table>
      <ProjectForm users={users} />
    </div>
  );
};

const ProjectListFilterId = ({
  users,
  projects,
  deleteProject,
  editProject,
}) => {
  let { id } = useParams();
  let filtered_items = projects.filter((item) => item.id.toString() === id);
  return (
    <ProjectList
      projects={filtered_items}
      deleteProject={deleteProject}
      editProject={editProject}
      users={users}
    />
  );
};

export { ProjectList, ProjectListFilterId };
